package com.ardor3d.extension.ui;import org.junit.Assert;import org.junit.Before;import org.junit.Test;public class UIComponentTest {    private UIComponent component;    @Before    public void setUp() throws Exception {        component = new UIComponent() {        };    }    @Test    public void minimumSizeSetsContentSize() throws Exception {        final int contentHeight = component.getContentHeight();        final int contentWidth = component.getContentWidth();        Assert.assertTrue(contentHeight < 200);        Assert.assertTrue(contentWidth < 100);        component.setMinimumContentSize(100, 200);        Assert.assertEquals(100, component.getMinimumLocalComponentWidth());        Assert.assertEquals(200, component.getMinimumLocalComponentHeight());        Assert.assertEquals(100, component.getContentWidth());        Assert.assertEquals(200, component.getContentHeight());    }    @Test    public void cantSetContentSizeSmallerThanMinimum() throws Exception {        final int minWidth = component.getMinimumLocalComponentWidth();        final int minHeight = component.getMinimumLocalComponentHeight();        final int contentHeight = component.getContentHeight();        final int contentWidth = component.getContentWidth();        Assert.assertTrue(contentWidth >= minWidth);        Assert.assertTrue(contentHeight >= minHeight);        component.setContentSize(minWidth - 2, minHeight - 1);        Assert.assertEquals(minWidth, component.getContentWidth());        Assert.assertEquals(minHeight, component.getContentHeight());    }    @Test    public void cantSetContentSizeLargerThanMaximum() throws Exception {        final int maxWidth = component.getMaximumLocalComponentWidth();        final int maxHeight = component.getMaximumLocalComponentHeight();        final int contentHeight = component.getContentHeight();        final int contentWidth = component.getContentWidth();        Assert.assertTrue(contentWidth <= maxWidth);        Assert.assertTrue(contentHeight <= maxHeight);        component.setContentSize(maxWidth + 2, maxHeight + 3);        Assert.assertEquals(maxWidth, component.getContentWidth());        Assert.assertEquals(maxHeight, component.getContentHeight());    }}